function ConvertNumberToPersion() { persian = { 0: "۰", 1: "۱", 2: "۲", 3: "۳", 4: "۴", 5: "۵", 6: "۶", 7: "۷", 8: "۸", 9: "۹" },
        function e(n) { if (3 == n.nodeType) { var o = n.data.match(/[0-9]/g); if (null != o && 0 != o.length)
                    for (var r = 0; r < o.length; r++) n.data = n.data.replace(o[r], persian[o[r]]) } for (r = 0; r < n.childNodes.length; r++) e(n.childNodes[r]) }(document.body) }
$(document).ready(function() { ConvertNumberToPersion() });